"""Legal disclaimer text for all user-facing output.

Single source of truth. Import ``DISCLAIMER_TEXT`` or call ``get_disclaimer()``
everywhere a disclaimer is needed. Never abbreviate or hardcode separately.
"""

from __future__ import annotations

DISCLAIMER_TEXT: str = (
    "DISCLAIMER: This analysis is generated by AI for educational and research purposes only. "
    "It does not constitute investment advice, a recommendation, or a solicitation to buy or "
    "sell any securities or options. Options trading involves substantial risk of loss and is "
    "not appropriate for all investors. Past performance and AI-generated analysis do not "
    "guarantee future results. Market data may be delayed or inaccurate. Always consult a "
    "qualified financial advisor before making investment decisions."
)


def get_disclaimer() -> str:
    """Return the full legal disclaimer text.

    Returns:
        The complete disclaimer string, identical to :data:`DISCLAIMER_TEXT`.
    """
    return DISCLAIMER_TEXT
